import React from 'react'
import Header from './Header'
import { Route, Routes, Navigate, useNavigate } from 'react-router-dom';
import Main from './Main'
import Login from './Login'
import InfoTooltip from './InfoTooltip'
import Register from './Register'
import ProtectedRoute from './ProtectedRoute'
import ImagePopup from './ImagePopup'
import { useEffect, useState } from 'react'
import PopupWithForm from './PopupWithForm'
import { CurrentUserContext } from '../contexts/CurrentUserContext'
import { api } from '../utils/api.js'
import { auth } from '../utils/auth.js'
import EditProfilePopup from './EditProfilePopup'
import EditAvatarPopup from './EditAvatarPopup'
import AddPlacePopup from './AddPlacePopup'
// import { Switch, Route } from 'react-router-dom'

function App() {
  const [isEditProfilePopupOpen, setEditProfileState] = useState(false);
  const [isAddPlacePopupOpen, setAddPlacePopupState] = useState(false);
  const [isEditAvatarPopupOpen, setEditAvatarPopupState] = useState(false);
  const [isImagePopupOpen, setImagePopupState] = useState(false);
  const [isInfoTooltipOpen, setIsInfoTooltipOpen] = useState(false);
  const [selectedCard, setSelectedCard] = useState({ link: '', name: '' });
  const [currentUser, setCurrentUser] = useState({});
  const [cards, setCards] = useState([]);
  const [isAuth, setIsAuth] = useState(false);
  const [userData, setUserData] = useState({});
  const history = useNavigate();

  function handleEditProfileClick() { setEditProfileState(!isEditProfilePopupOpen) }
  function handleAddPlaceClick() { setAddPlacePopupState(!isAddPlacePopupOpen) }
  function handleEditAvatarClick() { setEditAvatarPopupState(!isEditAvatarPopupOpen) }
  function handleCardClick(card) {
    setSelectedCard(card)
    setImagePopupState(true)
  }

  function closeAllPopups() {
    setEditProfileState(false)
    setAddPlacePopupState(false)
    setEditAvatarPopupState(false)
    setImagePopupState(false)
  }

  function handleSignUp({ email, password }) {
    auth.signUp(email, password).then((res) => {

      console.log('результат регистрации', res)
      handleSignIn({ email, password })
    })
      .catch((err) => {
        console.log('Ошибка регистрации', err)
      })
  }

  function handleSignIn({ email, password }) {
    auth.signIn(email, password).then((res) => {
      if (res.token) {
        localStorage.setItem('jwt', res.token);
        setIsAuth(true);
        tockenCheck();
      }
    }).catch((err) => {
      console.log('Ошибка входа', err);
    })
  }

  function redirectToLogin() {
    history('/login');
  }

  function redirectToRegister() {
    history('/register');
  }

  function tockenCheck() {
    if (localStorage.getItem('jwt')){
      let jwt = localStorage.getItem('jwt');
      auth.isAuth(jwt).then((res) => {

        if (res){
          let userData = {
            email: res.data.email
          }

          setIsAuth(true);
          setUserData(userData);

        }
      }).catch((err) => {
        console.log('Ошибка авторизации', err)
      });
    }
  }

  useEffect(() => {
    tockenCheck();
  }, [])

  function handleUpdateUser({ name, about }) {
    api.editProfile(name, about).then((res) => {
      setCurrentUser(res);
      closeAllPopups();
    }).catch((err) => {
      console.log('Ошибка обновления данных пользователя', err)
    })
  }

  function handleUpdateAvatar({ avatar }) {
    api.updAvatar(avatar).then((res) => {
      setCurrentUser(res);
      closeAllPopups();
    }).catch((err) => {
      console.log('Ошибка обновления аватара', err)
    })
  }

  function handleCardLike(card) {
    const isLiked = card.likes.some(i => i._id === currentUser._id);

    api.changeLikeCardStatus(card._id, !isLiked).then((newCard) => {
      setCards((cards) => cards.map((c) => c._id === card._id ? newCard : c));
    }).catch((err) => {
      console.log('Ошибка удаления карточки', err)
    });
  }

  function handleCardDelete(card) {
    api.deleteCard(card._id).then((data) => {
      setCards((cards) => cards.filter((c) => { return c._id !== card._id }))
    }).catch((err) => {
      console.log('Ошибка удаления карточки', err)
    });
  }

  function handleNewCardSubmit({ name, link }) {
    api.addCard(name, link).then((res) => {
      setCards([res, ...cards])
      closeAllPopups();
    }).catch((err) => {
      console.log('Ошибка добавления новых карточек', err)
    })
  }

  function signOut() {
    localStorage.removeItem('jwt');
    setIsAuth(false);
    setUserData({});
    history('/register');
  }

  useEffect(() => {
    if (isAuth) {
      history("/");
    }
}, [isAuth]);

  useEffect(() => {
    api.getProfile()
      .then(res => {
        setCurrentUser(res)
      }).catch((err) => {
        console.log('Ошибка загрузки данных профиля', err)
      })

  }, [])


  useEffect(() => {
    api.getInitialCards()
      .then(res => {
        setCards(res)
      }).catch((err) => {
        console.log('Ошибка загрузки данных карточек', err)
      })

  }, [])

  const hery = 'hery'

  return (
    <CurrentUserContext.Provider value={currentUser}>
      <div className="page">

        <Routes>
          <Route exact path="/login" isAuth={isAuth} element={<Login onSignIn={handleSignIn} tockenCheck={tockenCheck} redirectToRegister={redirectToRegister}/>}></Route>
          <Route exact path="/register" isAuth={isAuth} element={<Register onSignUp={handleSignUp} redirectToLogin={redirectToLogin}/>}></Route>
          <Route path="/" element={
            <ProtectedRoute isAuth={isAuth} hery={hery}>
              <Header isAuth={isAuth} userData={userData} title={'Выйти'} handleClick={signOut}/>
              <Main onEditProfile={handleEditProfileClick}
                onAddPlace={handleAddPlaceClick}
                onEditAvatar={handleEditAvatarClick}
                onCardClick={handleCardClick}
                cards={cards}
                onCardLike={handleCardLike}
                onCardDelete={handleCardDelete} />
            </ProtectedRoute>
          }></Route>
          <Route element={isAuth ? <Navigate to="/"/> : <Navigate to="/login"/>}></Route>
        </Routes>

        <InfoTooltip isOpen={isInfoTooltipOpen} onClose={closeAllPopups} onUpdateUser={handleUpdateUser} />
        <EditProfilePopup isOpen={isEditProfilePopupOpen} onClose={closeAllPopups} onUpdateUser={handleUpdateUser} />
        <EditAvatarPopup isOpen={isEditAvatarPopupOpen} onClose={closeAllPopups} onUpdateAvatar={handleUpdateAvatar} />
        <AddPlacePopup isOpen={isAddPlacePopupOpen} onClose={closeAllPopups} onAddPlace={handleNewCardSubmit} />
        <PopupWithForm name={"delete-card"} title={"Вы уверены?"} buttonText={"Да"} onClose={closeAllPopups} />
        <ImagePopup isOpen={isImagePopupOpen} card={selectedCard} onClose={closeAllPopups} />

      </div>
    </CurrentUserContext.Provider>
  );
}

export default App;
