import { CurrentUserContext } from '../contexts/CurrentUserContext'
import { useContext, useState } from 'react';

function Card({ cardData, onCardClick, onCardLike }) {
    const [activeLike, setActiveLike] = useState('card__like-button');
    const currentUser = useContext(CurrentUserContext);
    const isOwn = cardData.owner._id === currentUser._id;
    const isLiked = cardData.likes.some(i => i._id === currentUser._id);

    const cardDeleteButtonClassName = (
        `${isOwn ? 'card__delete-button' : 'card__delete-button_hidden'}`
    );
    // const cardLikeButtonClassName = (

    // );

    function handleClick(e) {
        const link = e.target.src;
        const name = e.target.alt;
        onCardClick({ link, name });
    }

    function handleLikeClick(e) {
        isLiked ? setActiveLike('card__like-button_active') : setActiveLike('card__like-button')
        onCardLike(cardData)
    }

    return (
        <div className="card">
            <button className={cardDeleteButtonClassName} type="button"></button>
            <img src={cardData.link} className="card__image" alt={cardData.name} onClick={
                (e) => { handleClick(e) }
            } />
            <div className="card__description">
                <h2 className="card__text">{cardData.name}</h2>
                <div className="card__like-section">
                    <button className={activeLike} type="button" onClick={handleLikeClick}></button>
                    <span className="card__like-number">{cardData.likes.length}</span>
                </div>
            </div>
        </div>
    );
}

export default Card;
