import React from 'react';
import AuthForm from './AuthForm';
import Header from './Header'

function Register({ onSignUp, redirectToLogin }) {

    const handleSubmit = (e) => {
        e.preventDefault();
        if (formParams.password === formParams.confirmPassword) {
            let { username, password, email } = formParams;
            props.handleRegister({ username, password, email }).catch(err => {
                setMessage(err.message);
            });
        }
    }

    return (
        <>
            <Header title={'Войти'} handleClick={redirectToLogin} />
            <AuthForm title={'Регистрация'} onSubmit={handleSubmit} buttonTitle={'Зарегистрироваться'} />
            <p className="header__text" >Уже зарегистрированы? <a className="header__text" href="#" onClick={redirectToLogin}>Войти</a></p>
        </>
    );
}

export default Register;
